function format(n,t,r){return(n||"")+(t?" "+t:"")+(r?" "+r:"")}function calc(n){var t=n.split(/\+|\-|\*|\//).map(function(n){return parseInt(n)}),r=n.split(/\d+/).filter(function(n){return""!==n});return null==t||null==r?0:t.length<=1||r.length<=0?0:t.length-r.length!=1?0:r.reduce(function(n,r,e){return _calc(n,t[e+1],r)},t[0])}function _calc(n,t,r){switch(r){case"+":return n+t;case"-":return n-t;case"*":return n*t;case"/":return n/t}}export{calc as a,format as b};